/** Accessor that spontaneously produces outputs once per time interval.
 *  This implementation produces a counting sequence.
 *
 *  @accessor test/Clock
 *  @parameter interval The interval between outputs in milliseconds.
 *  @output output Output for the counting sequence, of type number.
 *  @author Edward A. Lee
 *  @version $$Id: Clock.js 1924 2017-07-05 16:07:17Z chadlia.jerad $$
 */

// Stop extra messages from jslint.  Note that there should be no
// space between the / and the * and global.
/*globals clearInterval, exports, require, setInterval */
/*jshint globalstrict: true*/
"use strict";

exports.setup = function () {
    this.parameter('interval', {
        'type': 'number',
        'value': 1000
    });
    this.output('output', {
        'type': 'number'
    });
};

// These variables will not be visible to subclasses.
var handle = null;
var count = 0;

exports.initialize = function () {
    count = 0;
    // Need to record 'this' for use in the callback.
    var thiz = this;

    // Send an output and then call setInterval
    thiz.send('output', count);
    count += 1;

    handle = setInterval(function () {
        thiz.send('output', count);
        count += 1;
    }, this.getParameter('interval'));
};

exports.wrapup = function () {
    if (handle) {
        clearInterval(handle);
        handle = null;
    }
};

exports.setup = function() {
	this.extend('utilities/MutableBase');
	this.implement('ControllableSensor');
};

//This is a dummy accessor (it already contains all the information it
//"accesses") that is supposed to be a placeholder for a location accessor
//like RedPin that works at the room level. This accessor is supposed to
//output the room depicted in the AR image 


//Name of the spatial ontology used in this model
var spatialOntology = 'ARDemo';

var location = spatialOntology + "://" + 'Berkeley/CoryHall/DOPCenter';

exports.setup = function() {
    //Inputs
    this.input('trigger');

    //Outputs
    this.output('location', {
        'type': 'string'
    });
}

exports.initialize = function() {
    var thiz = this;

    this.addInputHandler('trigger' , function () {
        thiz.send( 'location', location);
    });
}

var httpClient = require('@accessors-modules/http-client');

var IdToResource = null;

exports.setup = function() {
    // Inputs
    this.input('tags');
    this.input('index', {
        'type':'int', 
        'value':0
    });

    // Outputs
    this.output('tag', {
        'type': 'int'
    });
    this.output('translation');
}

exports.initialize = function() {
    var thiz = this;
    var previousIndex = -1;


    //Assume both the tags and index inputs always arrive simultaneously
    this.addInputHandler('tags', function() {
        var tags = thiz.get('tags');
        var index = thiz.get('index');

        if (index != previousIndex) {
            // Index has changed. Look up a new tag.
            if (tags && tags.length > index) {
                thiz.send('tag', tags[index].id);
            }
            previousIndex = index;
        }
        thiz.send('translation', tags[index].center);
    });
}

//One-off javascript accessor for constructing a tag URI. Once a location
//has been given as input it will begin constructing tagURIs. Before then
//this accessor will not react to tag inputs.

var location = null;


exports.setup = function() {
    //Inputs
    this.input('location', {
        "type" : "string"
    });
    this.input('tag', {
        "type" : "int"
    });

    //Outputs
    this.output('tagURI', {
        "type" : "string",
        "value" : ""
    });
}

exports.initialize = function() {
    var thiz = this;

    this.addInputHandler('location' , function () {
        location = thiz.get('location');
    });

    this.addInputHandler('tag' , function () {
        if(location){
            var uri = location + "/" + thiz.get('tag');
            thiz.send( 'tagURI', uri);
        }
    });
}

//Dummy Accessor for an ontology map between tagURIs and accessors


var httpClient = require('@accessors-modules/http-client');

//A non-dummy implementation of this accessor would obtain this information
//from some external source and then cache it locally.
var IdToResource = {
    'ARDemo://Berkeley/CoryHall/DOPCenter/127': 'PressureSensor.js',
    'ARDemo://Berkeley/CoryHall/DOPCenter/435': 'VibrationSensor.js',
    'ARDemo://Berkeley/CoryHall/DOPCenter/105': 'TemperatureSensor.js'  
};

exports.setup = function() {
    // Inputs
    this.input('tagURI', {
        'type': 'string'
    });

    // Outputs
    this.output('accessor', {
        'type': 'string',
        'value': ''
    });
}

exports.initialize = function() {
    var thiz = this;

    this.addInputHandler('tagURI', function() {
        var uri = thiz.get('tagURI');

        var accessor = getResource(IdToResource[uri], 2000);
        if (accessor) {
            thiz.send('accessor', accessor);
        }
    });
}
