/** Test accessor that spontaneously produces outputs once per time interval.
 *  This implementation produces a counting sequence.
 *
 *  @accessor test/TestSpontaneousOnce
 *  @parameter delay The delay in milliseconds before the value is outputk.
 *  @parameter value The value to be output after the delay.
 *  @output output The output port
 *  @author Christopher Brooks and Edward A. Lee. Based on TestSpontaneous by Edward A. Lee
 *  @version $$Id: TestSpontaneousOnce.js 1659 2017-05-09 14:26:57Z cxh $$
 */

// Stop extra messages from jslint.  Note that there should be no
// space between the / and the * and global.
/*globals clearTimeout, console, error, exports, require, setTimeout */
/*jshint globalstrict: true*/
"use strict";

exports.setup = function () {
    this.parameter('delay', {
        'type': 'number',
        'value': 1000
    });
    this.parameter('value', {
        'value': true
    }); 
    this.output('output');
};

// These variables will not be visible to subclasses.
var handle = null;

exports.initialize = function () {
    // Need to record 'this' for use in the callback.
    var thiz = this;
    handle = setTimeout(function () {
        thiz.send('output', thiz.getParameter('value'));
    }, this.getParameter('delay'));
};

exports.wrapup = function () {
    if (handle) {
        clearTimeout(handle);
        handle = null;
    }
};

/** The key is the AprilTag ID, which is an integer and the
 *  value is the Hue ID.
 */
var IdToLifxId = [];

/** Setup the input and output ports.
 *  Read in the hue.json file and create an array that maps from AprilTag id
 *  to the hue ID name.
 */
exports.setup = function() {
    // Inputs
    this.input('trigger');

    // Outputs
    this.output('aprilTagId', {
        'type': 'string',
        'value': ''
    });
    this.output('accessor', {
        'type': 'string',
        'value': ''
    });


    this.parameter('file', {
    	'type': 'string',
    	'value': 'LifxAccessorTemplate.js'
    });

    // Read in the lifxs.json file and create an array that maps
	// AprilTag to Accessor.
	var TagToAccessor = [];

	var lifxs = JSON.parse(getResource('lifxs.json', 10000));
	for (lifx in lifxs.lifxs) {
		var theLifx = lifxs.lifxs[lifx];
		if (typeof theLifx.atag !== 'undefined') {
			var aprilTag = theLifx.atag;
			aprilTag = parseInt(aprilTag, 10);
			IdToLifxId[aprilTag] = theLifx;
			// console.log('TagToAccessor: AprilTag: ' + aprilTag + ', LifxId: ' + theLifx.macAddress);
		}
	}
};

exports.initialize = function() {
    var thiz = this;
    var aprilTagsSeen = [];

    // Each time we get a trigger, substitute in the macAddress, the ipAddress
    // and the port, then output the accessor.
    this.addInputHandler('trigger', function() { 
        // Instantiate a standAlone Lifx Accessor
        // in order to run Lifx discovery.
	    var discoverLifx = this.instantiate('discoverLifx', 'devices/Lifx', true);
	    discoverLifx.setParameter('discoveryInterval', 10000);
	    discoverLifx.initialize();
	    discoverLifx.provideInput('triggerDiscovery', 1);
	    discoverLifx.react();

	    var discoveredLifxBulbs;

	    var handleDiscovery = setInterval(function() {
	    	discoveredLifxBulbs = discoverLifx.latestOutput('discovered');
	    	if (discoveredLifxBulbs) {
	    		// Stop discovery
	    		clearInterval(handleDiscovery);

	    		// We can distroy now the discoverLifx
	    		discoverLifx.wrapup();
	    		discoverLifx = null;

	    		// Now generate the accessors
			    var lifxAccessorTemplate = getResource(getParameter('file', 2000));
		    	for (var aprilTag in IdToLifxId) {
		    		if (typeof aprilTagsSeen[aprilTag] === 'undefined' ) {
		    			aprilTagsSeen[aprilTag] = true;
		    			var macAddress = IdToLifxId[aprilTag].macAddress;
		    			discoveredLifxBulbs.forEach(function(lb) {
		    				if (lb.macAddress === macAddress) {
		    					var ipAddress = lb.ipAddress;
		    					var port = lb.port;
		    					var accessor = lifxAccessorTemplate.replace(/@ipAddress@/, ipAddress);
		        				var accessor = accessor.replace(/@macAddress@/, macAddress);
		        				var accessor = accessor.replace(/@port@/, port);
		        				thiz.send('aprilTagId', aprilTag);
		        				thiz.send('accessor', accessor);
		    				}
		    			});
		    		}
		    	}
		    	// We are done! 
		    	thiz.wrapup();
	    	};
	    }, 2000);
    });
};
