// Add ports and parameters.
// Define JavaScript functions initialize(), fire(), and/or wrapup().
// Refer to parameters in scope using dollar-sign{parameterName}.
// In the fire() function, use get(parameterName, channel) to read inputs.
// Send to output ports using send(value, portName, channel).

exports.setup = function() {
	this.input('request');
	this.output('help');
	this.output('list');
	this.output('other');
};

exports.initialize = function() {
	var self = this;
	this.addInputHandler('request', function() {
		var request = self.get('request');
		if (request.path !== null && typeof request.path !== 'undefined') {
			if (request.path == '/keyvalue/help') {
				self.send('help', request);
			} else if (request.path == '/keyvalue/list') {
				self.send('list', request);
			} else {
				self.send('other', request);
			}
		} else {
			self.send('other', request);
		}
	});
};

exports.setup = function() {
	this.input('request');
	this.input('keys');
	this.output('response');
}
exports.initialize = function() {
	var self = this;
	this.addInputHandler('keys', function() {
		var keys = self.get('keys');
		var requestID = self.get('request').requestID;
		var response = {
			requestID: requestID,
			response: keys
		};
		self.send('response', response);
	});
}

// Put your JavaScript program here.
// Add ports and parameters.
// Define JavaScript functions initialize(), fire(), and/or wrapup().
// Refer to parameters in scope using dollar-sign{parameterName}.
// In the fire() function, use get(parameterName, channel) to read inputs.
// Send to output ports using send(value, portName, channel).

exports.setup = function() {
	this.input('request');
	this.output('readKey', {'type': 'string'});
	this.output('value', {'type': 'string'});
	this.output('writeKey', {'type': 'string'});
	this.output('response', {'type': 'string'});
	this.output('responseCode', {'type': 'int'});
};

exports.initialize = function() {
	var self = this;
	this.addInputHandler('request', function() {
		var request = self.get('request');

		if (request !== null && typeof request !== 'undefined' &&
				request.path !== null && typeof request.path !== 'undefined') {
			var path = request.path;
			var parameters = request.params;

			if (path.indexOf('/keyvalue/get') == 0) {
				// Request is get.
				if (parameters.id) {
					self.send('readKey', parameters.id);
				} else {
					self.send('response',
	      				'<html>Error (400): Bad Request. Got parameters: '
	      				+ JSON.stringify(parameters)
	      				+ '</html>');
      				self.send('responseCode', 400);
				}
			} else if(path.indexOf('/keyvalue/set') == 0) {
				// Request is set.
				if (parameters.id) {
      				self.send('writeKey', parameters.id);
      				// For backward compatibility, a value parameter is allowed.
      				if (typeof parameters.value !== 'undefined') {
      					self.send('value', parameters.value);
      				} else {
      					// Assume a post request, in which case the value
      					// is in the body.
      					self.send('value', request.body);
      				}
      				self.send('response',
      					'<html>'
      					+ parameters.id
      					+ ' set to: '
      					+ parameters.value
      					+ '</html>');
		    	} else {
		      		self.send('response',
		      			'<html>Error (400): Bad Request. Got parameters: '
		      			+ JSON.stringify(parameters)
		      			+ '</html>');
		      		self.send('responseCode', 400);
		    	}
			} else if(path.indexOf('/keyvalue/delete') == 0) {
				// Request is delete.
				if (parameters.id) {
      				self.send('writeKey', parameters.id);
      				self.send('response',
      					'<html>' + parameters.id
      					+ ' deleted from store</html>');
    			} else {
      				self.send('response',
      					'<html>Error (400): Bad Request. Got parameters: '
      					+ JSON.stringify(parameters)
      					+ '</html>');
      				self.send('responseCode', 400);
    			}
			} else {
				// Request is not recognized.
				self.send('response', '<html>Error (400): Unrecognized Request. Got URI: ' + path + '</html>');
      			self.send('responseCode', 400);
			}

		} else {
			// Request is not recognized.
      		self.send('response', '<html>Error (400): Empty Request. </html>');
      		self.send('responseCode', 400);
		}
	});
};

exports.setup = function() {
	this.input('getResult');
	this.input('request');
	this.input('responseBody');
	this.input('responseCode');
	this.input('notFound');
	this.output('response');
}
exports.initialize = function() {
	var self = this;

	// Send a response for set and delete requests.
	this.addInputHandler('responseBody', function() {
		console.log('handling body');
		var requestID = self.get('request').requestID;
		var response = {
			requestID: requestID,
			response: self.get('responseBody')
		};

		var responseCode = self.get('responseCode');
		if (responseCode !== null) {
			response.responseCode = responseCode;
		}

		self.send('response', response);
	});

	// Send a response for a successful get request.
	this.addInputHandler('getResult', function() {
		var value = self.get('getResult');
		var requestID = self.get('request').requestID;
		var response = {
			requestID: requestID,
			response: value
		}
		self.send('response', response);
	});

	// Send a response for an unsuccessful get request.
	this.addInputHandler('notFound', function() {
		var value =  '<html>Error (404): Not Found.  No value for key.';
		var requestID = self.get('request').requestID;
		var response = {
			requestID: requestID,
			response: value,
			responseCode: 404
		} 
		self.send('response', response);
	});
}
