/** Accessor that spontaneously produces outputs once per time interval.
 *  This implementation produces a counting sequence.
 *
 *  @accessor utilities/Clock
 *  @parameter interval The interval between outputs in milliseconds.
 *  @output output Output for the counting sequence, of type number.
 *  @author Edward A. Lee
 *  @version $$Id$$
 */

// Stop extra messages from jslint.  Note that there should be no
// space between the / and the * and global.
/*globals clearInterval, exports, require, setInterval */
/*jshint globalstrict: true*/
"use strict";

exports.setup = function () {
    this.parameter('interval', {
        'type': 'number',
        'value': 1000
    });
    this.output('output', {
        'type': 'number'
    });
};

// These variables will not be visible to subclasses.
var handle = null;
var count = 0;

exports.initialize = function () {
    count = 0;
    // Need to record 'this' for use in the callback.
    var thiz = this;

    // Send an output and then call setInterval
    thiz.send('output', count);
    count += 1;

    handle = setInterval(function () {
        thiz.send('output', count);
        count += 1;
    }, this.getParameter('interval'));
};

exports.wrapup = function () {
    if (handle) {
        clearInterval(handle);
        handle = null;
    }
};

// Put your JavaScript program here.
// Refer to parameters in scope using dollar-sign{parameterName}.
// See: https://wiki.eecs.berkeley.edu/accessors/Version1/AccessorSpecification

// Put your JavaScript program here.
// Refer to parameters in scope using dollar-sign{parameterName}.
// See: https://wiki.eecs.berkeley.edu/accessors/Version1/AccessorSpecification

exports.setup = function() {
	this.mutable(true);
	this.input('trigger', {
		'type': 'number',
		'value': 0
	});
	this.output('whatIsGoingOn', {
		'type': 'string',
		'value': ''
	});
};

var i = 0;

exports.initialize = function() {
	var thiz = this;

	this.addInputHandler('trigger', function() {
		thiz.unreify();

		//
		resetInterface(thiz);

		if (i % 2 === 0) {
			var acc = thiz.instantiate('acc0', 'ControllableDevice', true);
			acc.exports.setup.call(thiz);
			thiz.reify(acc);
		} else {
			var acc = thiz.instantiate('acc1', 'AnotherControllableDevice', true);
			acc.exports.setup.call(thiz);
			thiz.reify(acc);
		}
		i++;
		var whatIsGoingOn = 'Now, the list of inputs is: ' +
				thiz.inputList + 
				' and the list of outputs is: ' +
				thiz.outputList;
		thiz.send('whatIsGoingOn', whatIsGoingOn);
	});
};

/** Function that resets the original interface, given in the setup.
 * 
 */
var resetInterface = function(thiz) {
	// Remove all inputs
	while(thiz.inputList.length) {
    	thiz.inputList.pop();
	}
	thiz.inputs = {};

	// Remove all outputs	
	while(thiz.outputList.length) {
    	thiz.outputList.pop();
	}
	thiz.outputs = {};

	// Reset the original interface by calling exports.setup() function
	thiz.exports.setup.call(thiz);
};

