// Accessor for bluetooth sensor stream.
var imuSensor = require('IMUSensor');

// Set up the accessor.
exports.setup = function() {
    accessor.author('Hunter');
    accessor.version('0.1 $$Date$$');
    this.parameter('port', {
            'type' : 'number',
            'description' : 'The port used by the sensor'
        });
    this.parameter('samplingRate', {'description':'Sampling rate of sensor, rate to grab data from buffer in ms'});
    this.output('stream', {
                'description' : 'The stream obtained from the sensor'
        });
};

var stream = new imuSensor.Stream();
var handle;

// Initialize by hooking to sensor port and setting interval for obtaining samples
exports.initialize = function() {
    // Instantiate a stream
    // This should hook the sensor stream and start filling a buffer Java-side automatically
    stream.start(getParameter('port'));
    handle = setInterval(getLatestSample.bind(this), this.getParameter('samplingRate'));
}

var prevSample = [[0,0,0,0,0,0,0,0,0],[]];

// function to invoke on interval, grab a sample from the buffer. Have to determine if sample is a new one.
var getLatestSample = function() {
    var sample = stream.getSample();
        if (prevSample[0][0] == sample[0][0]) {
        }
        else {
           prevSample = sample;
           sample = sample[0].map(Number);

           // convert the samples to real values from their ADC values
           sample[0] = sample[0]/16384;
           sample[1] = sample[1]/16384;
           sample[2] = sample[2]/16384;
           sample[3] = sample[3]/131;
           sample[4] = sample[4]/131;
           sample[5] = sample[5]/131;
           sample[6] = sample[6]/3.3;
           sample[7] = sample[7]/3.3;
           sample[8] = sample[8]/3.3;
           sample = JSON.stringify([sample,[]]);
           this.send('stream', sample);
        }
}

// Called on stop
exports.wrapup = function() {
    if (stream != null) {
        stream.stop();
    }
    clearInterval(handle);
}
