PREFIX adapters: <http://ptolemy.berkeley.edu/adapters#>

select * where{
    ?a adapters:MatchesTo adapters:Fahrenheit.
    ?a adapters:MatchesFrom adapters:Kelvin.
    ?accessorURI adapters:Implements ?a.
}

