property.value:
var webSocket = require('webSocketServer');
var server = null;
var clients = {};

exports.setup = function() {
	this.output('log');

	this.parameter('hostInterface', {
        value: "localhost",
        type: "string"
    });
    this.parameter('port', {
        value: 8080,
        type: "int"
    });
}

exports.initialize = function() {
	clients = {};
	server = new webSocket.Server({
        'port': this.getParameter('port'),
        'hostInterface': this.getParameter('hostInterface')
    });
	var self = this;
	server.on('connection', function(socket) {
    	send('log', 'New connection.');
    	var name = null;
    	socket.on('message', function(message) {
    		if (name === null) {
    			if (clients[message]) {
					send('log', 'Client requested duplicate name: ' + message);
					socket.send('Name is taken. Please choose another.');
    			} else {
    				name = message;
    				socket.send('Hello ' + name);
    				self.exports.sendToAll.call(this,
    						'Server', name + ' has joined');
    				clients[name] = socket;
    				send('log', 'New client joined: ' + name);
    			}
    		} else {
    			self.exports.sendToAll.call(self, name, message);
    		}
    	});
    	socket.on('close', function() {
    		// Remove the name so it can be reused.
    		if (name !== null) {
    			send('log', 'Client left: ' + name);
    			delete clients[name];
    		}
    	});
    	socket.send('What is your name?');
    });
    server.on('error', function (message) {
        self.send('log', 'ERROR: ' + message);
    });
    server.start();
}

exports.sendToAll = function(name, message) {
	for (var client in clients) {
		clients[client].send(name + ' says: ' + message);
		send('log', 'Sending message to ' + client + ': ' + message);
	}
}

exports.wrapup = function(){
    if (server) {
        server.stop();
    }
};
