/** This accessor advertises and publishes to a ROS topic. It extends
 * the WebSocketClient to communicate to a rosbridge websocket. <br>
 *
 * During intialize, it will advertise the topic it will publish to,
 * along with its type. The purpose of 'advertise' here is to
 * establish the ROS topic if isn't already established. This is
 * slightly different from the 'advertise' function in NodeHandle
 * which simply publicizes that the the node will be publishing
 * messages to the topic.
 * <ul>
 * <li>If the topic does not already exist, and the type is valid, a
 * topic will be established with this type.</li>
 * <li>If the topic already exists with a different type, no new topic
 * will be established</li>
 * <li>If the topic already exists with the same type, no new topic
 * will be established.</li>
 * <li>If the topic already exists but the type isn't resolved, no new
 * topic will be established.</li>
 * </ul>
 *
 *  On input from 'toSend', this accessor publishes that input to the
 *  aforementioned topic.
 *  The input from 'toSend' must be in JSON form, and must match the message
 *  datatype that the ROS topic is expecting. For example if ROS topic is
 *  expecting <code>std_msgs/String</code>, then the input in 'toSend' should be
 *  as follows:
 *  <pre> { "data": "hello world" } </pre>
 *  Some ROS types contain a header which consists of a sequence,
 *  timestamp, and a frame_id. If the ROS type has a header, you can
 *  either:
 *  <ul>
 *  <li>send the message on toSend without the header, and check the
 *  parameter 'addHeader'. This sends the message with a header with
 *  only the frame_id specified, and rosbridge will add the sequence
 *  and timestamp for you.</li>
 *  <li>send a fully formed message with all fields in the header
 *  included and don't check the parameter 'addHeader'.</li>
 *  </ul>
 *  This accessor doesn't do any error checking. All error messages
 *  orginate from rosbridge and will appear on the console running
 *  rosbridge. More information ccan be viewed on the <a href='https://github.com/RobotWebTools/rosbridge_suite/blob/groovy-devel/ROSBRIDGE_PROTOCOL.md'>rosbridge
 *  protocol specification</a> site.
 *
 *  @accessor robotics/RosPublisher
 *  @parameter {string} topic The ROS topic to publish to.
 *  @parameter {string} ROStype The ROS datatype of the topic.
 *  @parameter {boolean} addHeader If (true), this accessor will send a header with a blank seq and timestamp field, and rosbridge will add the header for you.
 *  @parameter {string} frame_id The frame_id of the header (only needed if a header is required).
 *  @author Marcus Pan
 *  @version $$Id$$
 */

// Stop extra messages from jslint and jshint.  Note that there should
// be no space between the / and the * and global. See
// https://chess.eecs.berkeley.edu/ptexternal/wiki/Main/JSHint */
/*globals exports, extend, get, getParameter, parameter */
/*jshint globalstrict: true*/
'use strict';

/** Sets up by accessor by inheriting inputs from setup() in
 * WebSocketClient. Adds additional parameters regarding the ROS topic
 * to which to publish to.
 */
exports.setup = function () {
    this.extend('net/WebSocketClient');
    this.parameter('topic', {
        type: "string"
    });
    this.parameter('ROStype', {
        type: "string"
    });
    this.parameter('addHeader', {
        type: "boolean",
        value: false
    });
    this.parameter('frame_id', {
        type: "string",
        value: ""
    });
};

/** Override inputHandler on 'toSend' from WebSocketClient. */
exports.toSendInputHandler = function () {
    var msg = this.get('toSend');
    // Add a header with a blank time and sequence info. This will be added by rosbridge.
    if (this.getParameter('addHeader')) {
        msg.header = {
            "frame_id": this.getParameter('frame_id')
        };
    }

    var data = {
        "op": "publish",
        "topic": this.getParameter('topic'),
        "msg": msg
    };

    this.exports.sendToWebSocket.call(this, data);
};

/**  Inherits initialize from WebSocketClient.
 *   Advertise the topic we are publishing to.
 */
exports.initialize = function () {
    this.exports.ssuper.initialize.call(this);

    var advertise = {
        "op": "advertise",
        "topic": this.getParameter('topic'),
        "type": this.getParameter('ROStype')
    };
    this.exports.sendToWebSocket.call(this, advertise);

};

/** Unadvertise the topic and inherit wrapup from WebSocketClient. */
exports.wrapup = function () {
    var unadvertise = {
        "op": "unadvertise",
        "topic": this.getParameter('topic')
    };
    this.exports.sendToWebSocket.call(this, unadvertise);
    this.ssuper.wrapup();
};

var moveResultHandle, tagDetectionsMappedHandle, tfHandle, sendToTagHandle;
var cameraCoords;
var currentTag = {id: 0};

//vars to enumerate options for sendToTag function
var current = 0;
var next = 1;

var unreachableCount = 0;

exports.setup = function() {
   this.input('moveResult', {type: 'JSON', value: {}});
   this.input('pose', {type: 'JSON', value: {}});
   this.input('tagDetectionsMapped', {type: 'JSON', value: {}});
   this.input('tf', {type: 'JSON', value: {}});//frame transforms
   this.output('waypoint', {type: 'JSON'});//pose msg to publish to /goal topic
   this.output('cancelWaypoint', {type: 'JSON'});//abort msg to publish to /move/cancel topic
   this.output('lightID', {type: 'int'});
   this.output('hue', {type: 'int'});//hue of a Hue bulb
   this.output('on', {type: 'boolean'});
   this.output('trigger');
   this.parameter('stopAtTagDuration', {type: 'number', value: 1000}); //time to spend at each tag before moving in ms
   this.parameter('robotDistanceFromTag', {type: 'number', value: 0.1}); //distance from robot to tag in m 
   this.parameter('goalTolerance', {type: 'number', value: 0.2}); //the tolerance in distance (m) to conclude a robot has reached a tag
   this.parameter('mapFrame', {type: 'string', value: 'map_hokuyo'});
   this.parameter('cameraFrame', {type: 'string', value: 'foscam'});
}


exports.initialize = function() {
   tfHandle = this.addInputHandler('tf', tfHandler);
   tagDetectionsMappedHandle = this.addInputHandler('tagDetectionsMapped', 
         tagDetectionsMappedHandler);
   moveResultHandle = this.addInputHandler('moveResult', moveResultHandler);
}
/** search transfroms for camera coords. Removes input handler after set */
function tfHandler() {
   console.log('looking for camera to  map transform');
   //check transforms for camera pose
   var transforms = this.get('tf').msg.transforms;
   var map_frame = this.getParameter('mapFrame');
   var camera_frame = this.getParameter('cameraFrame');
   for (var i = 0; i < transforms.length; i++) {
      if (transforms[i].header.frame_id == map_frame && 
         transforms[i].child_frame_id == camera_frame) {
         cameraCoords = {"x": transforms[i].transform.translation.x,
                         "y": transforms[i].transform.translation.y};
         console.log("set camera coords");
         //only need to set this once
         this.removeInputHandler(tfHandle);
      }
   }

}
/**
 Only need a handle once. sendToTag is called next by the MoveStatusHandler
*/
function tagDetectionsMappedHandler() {
   if (!cameraCoords) {
      console.log('received tag detections but not camera coords yet. Will wait.');
      return;
   }
   sendToTag(next, this.getParameter('robotDistanceFromTag'));
   this.removeInputHandler(tagDetectionsMappedHandle);
}

/** Checks the move status of the hfn goal/status topic. The status is 
  defined by the ROS actionlib and the ROS message at scarab_msgs/action/Move.action
  If hfn goal is reached, check that tag is really within tolerance of the robot. 
  If not, resend to robot to current tag.
*/

function moveResultHandler() {
   var moveResult = this.get('moveResult').msg;
   console.log(moveResult);
   var moveStatus = this.get('moveResult').msg.result.final_status;
   var goalID  = moveResult.goal_id;//id of goal
   var finalStatus = moveResult.result.final_status//integer reporting status
   var robotDistanceFromTag = this.getParameter('robotDistanceFromTag');
   //moveStatus mapping: 0=FINISHED, 1=TIMEOUT, 2=STUCK, 3=NOTREADY, 4=UNREACHABLE
   if (moveStatus == 0) {
   //success
      flashLight(currentTag.id);
      unreachableCount = 0;
      sendToTag(next);
      sendToTagHandle = setTimeout(sendToTag, this.getParameter('stopAtTagDuration'), next, robotDistanceFromTag);
      console.log('robot reached waypoint successfully. sending to next tag');
   } else if (finalStatus == 4) {
   //waypoint is unreachable. tag localization may not be accurate, resulting in a tag location deep in occupied space.  
   //abort current waypoint and send new one with increased distance between waypoint and tag
      cancelWaypoint();
      unreachableCount++;
      sendToTag(current, unreachableCount * robotDistanceFromTag);
      console.log('waypoint cannot be reached. sending new one with increased distance betweeen waypoint and tag');
    } else {
     //problem encountered. abort previous goals and  send to current tag again. 
      cancelWaypoint();
      sendToTag(current, robotDistanceFromTag);
      console.log('robot is either stuck, not ready or too slow. sending waypoint again');
   }
}

/** Utility functions to flash lights */
function flashLight(id) {
   this.send('lightID', id);
   this.send('hue', 62580 * id/3);
   this.send('on', true);
   this.send('trigger', true);
   setTimeout(offLight, 2000, id);
}

function offLight(id) {
   this.send('lightID', id);
   this.send('on', false);
   this.send('trigger', true);
}


/** Assume currentTag is still in the frame and get nextTag by ascending ID.
    If stayOnCurrentTag is defined and true, nextTag is the current tag.
    If can't get currentTag, set nextTag to the first tag. 
    Send to nextTag's position. 
    tag0 is on the robot. 
*/ 
function sendToTag(currentOrNext, distanceFromTag) {
   var input = this.get('tagDetectionsMapped');  
   console.log(input);
   if (!input.msg) {
      console.log('No detections received yet. Will check again');
      setTimeout('sendToTag', 1000);
      return;
   }
   if (!cameraCoords) {
      console.log('No camera coords yet.  Will check again');
      setTimeout('sendToTag', 1000);
      return;
   }

   var detections = input.msg.detections;
   detections.sort(function(a, b) {return a.id > b.id});
   console.log('got detections');

   for (var i = 0; i < detections.length; i++) {
      if (detections[i].id == currentTag.id) {
         if (currentOrNext == current) { 
            currentTag = detections[i];
         } else {
            currentTag = detections[(i+1) % detections.length];
         }
         break;
      }
      //current tag no longer exists. set to tag with smallest id.
      //don't send to tag0 (on the robot)
      if (i == detections.length - 1) {
         currentTag = detections[1];
      }
   } 
   //tag frame: facing tag, x->right, y->up, z->toward you
   //robot frame: facing robot, x->toward you, y->right, z->up
   //send robot to face tag with separation specified by parameter, in the line from the camera to the tag
   //format data accoding to ROS pose datatype, (cartesian position and quaternion orientation)
   tagX = currentTag.pose.pose.position.x;
   tagY = currentTag.pose.pose.position.y;
   cameraX = cameraCoords.x;
   cameraY = cameraCoords.y;

   //get unit vector of direction from tag to camera
   deltaX = cameraX - tagX;
   deltaY = cameraY - tagY;
   deltaLength = distance(deltaX, 0, deltaY, 0);

   theta = Math.atan2(deltaY, deltaX) + Math.PI;

   goal = {
      pose: {
         position: {
            x: tagX + (distanceFromTag * deltaX / deltaLength), 
            y: tagY + (distanceFromTag * deltaY / deltaLength),
            z: 0
         },
         orientation: {
            x: 0,
            y: 0,
            z: Math.sin(0.5*theta),  
            w: Math.cos(0.5*theta)
         }
      }
   }
   console.log('waypoint sent to tag ' + currentTag.id);
   this.send('waypoint', goal); 

}

function cancelWaypoint() {
   //blank info aborts all goals for all time. This is fine, since we only send one goal at a time.
   msg = {
      stamp: {
         secs: 0,
         nsecs: 0
      },
      id: ''
   }
   this.send('cancelWaypoint', msg);
}

exports.wrapup = function() {
   //abort all goals
   cancelWaypoint();
   //remove input handlers
   if (moveResultHandle) {
     this.removeInputHandler(moveResultHandle);
   } 
   if (tagDetectionsMappedHandle) {
     this.removeInputHandler(tagDetectionsMappedHandle);
   } 
   if (tfHandle) {
     this.removeInputHandler(tfHandle);
   } 
   if (sendToTagHandle) {
     this.removeInputHandler(sendToTagHandle);
   } 
}

/** utlity function to return distance between two points */
function distance(x1, x2, y1, y2) {
   return Math.sqrt(
      Math.pow(x2 - x1, 2) + Math.pow(y2 -y1, 2)
   );
}
