/** This accessor advertises and publishes to a ROS topic. It extends
 * the WebSocketClient to communicate to a rosbridge websocket. <br>
 *
 * During intialize, it will advertise the topic it will publish to,
 * along with its type. The purpose of 'advertise' here is to
 * establish the ROS topic if isn't already established. This is
 * slightly different from the 'advertise' function in NodeHandle
 * which simply publicizes that the the node will be publishing
 * messages to the topic.
 * <ul>
 * <li>If the topic does not already exist, and the type is valid, a
 * topic will be established with this type.</li>
 * <li>If the topic already exists with a different type, no new topic
 * will be established</li>
 * <li>If the topic already exists with the same type, no new topic
 * will be established.</li>
 * <li>If the topic already exists but the type isn't resolved, no new
 * topic will be established.</li>
 * </ul>
 *
 *  On input from 'toSend', this accessor publishes that input to the
 *  aforementioned topic.
 *  The input from 'toSend' must be in JSON form, and must match the message
 *  datatype that the ROS topic is expecting. For example if ROS topic is
 *  expecting <code>std_msgs/String</code>, then the input in 'toSend' should be
 *  as follows:
 *  <pre> { "data": "hello world" } </pre>
 *  Some ROS types contain a header which consists of a sequence,
 *  timestamp, and a frame_id. If the ROS type has a header, you can
 *  either:
 *  <ul>
 *  <li>send the message on toSend without the header, and check the
 *  parameter 'addHeader'. This sends the message with a header with
 *  only the frame_id specified, and rosbridge will add the sequence
 *  and timestamp for you.</li>
 *  <li>send a fully formed message with all fields in the header
 *  included and don't check the parameter 'addHeader'.</li>
 *  </ul>
 *  This accessor doesn't do any error checking. All error messages
 *  orginate from rosbridge and will appear on the console running
 *  rosbridge. More information ccan be viewed on the <a href='https://github.com/RobotWebTools/rosbridge_suite/blob/groovy-devel/ROSBRIDGE_PROTOCOL.md'>rosbridge
 *  protocol specification</a> site.
 *
 *  @accessor robotics/RosPublisher
 *  @parameter {string} topic The ROS topic to publish to.
 *  @parameter {string} ROStype The ROS datatype of the topic.
 *  @parameter {boolean} addHeader If (true), this accessor will send a header with a blank seq and timestamp field, and rosbridge will add the header for you.
 *  @parameter {string} frame_id The frame_id of the header (only needed if a header is required).
 *  @author Marcus Pan
 *  @version $$Id$$
 */

// Stop extra messages from jslint and jshint.  Note that there should
// be no space between the / and the * and global. See
// https://chess.eecs.berkeley.edu/ptexternal/wiki/Main/JSHint */
/*globals exports, extend, get, getParameter, parameter */
/*jshint globalstrict: true*/
'use strict';

/** Sets up by accessor by inheriting inputs from setup() in
 * WebSocketClient. Adds additional parameters regarding the ROS topic
 * to which to publish to.
 */
exports.setup = function () {
    this.extend('net/WebSocketClient');
    this.parameter('topic', {
        type: "string"
    });
    this.parameter('ROStype', {
        type: "string"
    });
    this.parameter('addHeader', {
        type: "boolean",
        value: false
    });
    this.parameter('frame_id', {
        type: "string",
        value: ""
    });
};

/** Override inputHandler on 'toSend' from WebSocketClient. */
exports.toSendInputHandler = function () {
    var msg = this.get('toSend');
    // Add a header with a blank time and sequence info. This will be added by rosbridge.
    if (this.getParameter('addHeader')) {
        msg.header = {
            "frame_id": this.getParameter('frame_id')
        };
    }

    var data = {
        "op": "publish",
        "topic": this.getParameter('topic'),
        "msg": msg
    };

    this.exports.sendToWebSocket.call(this, data);
};

/**  Inherits initialize from WebSocketClient.
 *   Advertise the topic we are publishing to.
 */
exports.initialize = function () {
    this.exports.ssuper.initialize.call(this);

    var advertise = {
        "op": "advertise",
        "topic": this.getParameter('topic'),
        "type": this.getParameter('ROStype')
    };
    this.exports.sendToWebSocket.call(this, advertise);

};

/** Unadvertise the topic and inherit wrapup from WebSocketClient. */
exports.wrapup = function () {
    var unadvertise = {
        "op": "unadvertise",
        "topic": this.getParameter('topic')
    };
    this.exports.sendToWebSocket.call(this, unadvertise);
    this.ssuper.wrapup();
};

/** 
 * This accessor controls the Scarab movements based on arrow keypresses.
 * It takes in keypress inputs and the current position of the Scarab robot.
 * 'U' moves the robot forward, 'L' rotates left, 'R' rotates right, and
 * 'D' returns to origin.
 * It then computes and outputs the next waypoint to the Scarab.  
 * This accessor does not do any publishing or subscribing. It should take in inputs  
 * from a RosSubscriber accessor, subscribed to the topic /pose. 
 * It should output the next waypoint to a ScarabGoalPublisher accessor. 
 *
 *  @accessor ScarabArrowKeyController
 *  @input {integer} U input the number (1) when Up arrow is pressed.
 *  @input {integer} L input the number (1) when Up arrow is pressed.
 *  @input {integer} D input the number (1) when Up arrow is pressed.
 *  @input {integer} R input the number (1) when Up arrow is pressed.
 *  @input {JSON} pose Current pose of the Scarab formatted as the ROS datatype geometry_msgs/Pose. 
 *  @output {JSON} newGoal Waypoint formatted according to the ROS datatype geometry_msgs/Pose.
 *  @author Marcus Pan 
 */


//change in radius and angle for each step
var deltaRadius = 0.5;
var deltaTheta = 0.5;

//current cartesian position (w is angle) 
var px, py, pz, pw; 
//current quaternion orientation
var qx, qy, qz, qw;

var handleU, handleR, handleL, handleD;

/** Define inputs and outputs */
exports.setup = function() {
  this.input('U', {
    type: "int",
    value: 0
  });
  this.input('L', {
    type: "int",
    value: 0
  });
  this.input('R', {
    type: "int",
    value: 0
  });
  this.input('D', {
    type: "int",
    value: 0
  });
  this.input('pose');
  this.output('newGoal');
}


/** Attach function hanlders to key inputs. */
exports.initialize = function() {
  handleU = this.addInputHandler('U', uHandler);
  handleR = this.addInputHandler('R', rHandler);
  handleL = this.addInputHandler('L', lHandler);
  //D returns to origin
  handleD = this.addInputHandler('D', function() {
      sendNewGoal(0, 0, 0, 1, 0, 0, 0);
  });
}

/** Format waypoint according to ROS datatype getometry_msgs/Pose */
function sendNewGoal(px, py, pz, qw, qx, qy, qz) {
    var newGoal = {
      "pose": {
         "position": {
           "x": px,
           "y": py,
           "z": pz 
         },
         "orientation": {
           "w": qw, 
           "x": qx, 
           "y": qy,
           "z": qz
         }
      } 
    };
    this.send('newGoal', newGoal);
}

/** Load global vars with current pose information. */
function getCurrentPose() {
  var currentPose = this.get('pose');
  if (currentPose == null) {
    console.log('null pose');
  } else {
    qw = currentPose.msg.pose.orientation.w;
    qx = currentPose.msg.pose.orientation.x;
    qy = currentPose.msg.pose.orientation.y;
    qz = currentPose.msg.pose.orientation.z;
    px = currentPose.msg.pose.position.x;
    py = currentPose.msg.pose.position.y;
    pz = currentPose.msg.pose.position.z;
    //Scarab is a ground robot, so only rotation about z-axis is understood
    if (qx != 0 || qy != 0) {
      console.err('Rotation must be about z-axis.');
    }
    //get euclidean angle (0 deg faces x-axis), theta,  of current robot orientation, 
    //based on the following formulas:
    //qw = cos(0.5*theta), qz = sin(0.5*theta)
    var theta = 2*Math.acos(qw);
    if (qw > 0) {
      if (qz > 0) pw = theta;
      else pw = -theta;
    } else {
      if (qz > 0) pw = theta;
      else pw = -theta;
    }
  }
}
/** Move forward by deltaRadius. */ 
function uHandler() { 
  if (get('U') == 1) { 
    getCurrentPose();
    sendNewGoal(px + deltaRadius*Math.cos(pw), py + deltaRadius*Math.sin(pw), 0.0, 
      qw, 0.0, 0.0, qz); 
  }
}

/** Rotate left by detlaTheta. */
function lHandler() {
  if (get('L') == 1) {
    getCurrentPose();
    var phi = 0.5*(pw + deltaTheta);
    sendNewGoal(px, py, 0.0, 
      Math.cos(phi), 0.0, 0.0, Math.sin(phi)); 
  }
}

/** Rotate right by detlaTheta. */
function rHandler() {
  if (get('R') == 1) {
    getCurrentPose();
    var phi = 0.5*(pw - deltaTheta);
    sendNewGoal(px, py, 0.0,
      Math.cos(phi),0.0, 0.0, Math.sin(phi)); 
  }
}

/** Remove input handlers. */
function wrapup() {
  this.removeInputHandler(handleU, 'U');
  this.removeInputHandler(handleR, 'R');
  this.removeInputHandler(handleL, 'L');
  this.removeInputHandler(handleD, 'D');
}
