property.value:
exports.setup = function() {
	this.input('tags');
	this.parameter('speed', {type: 'string', value: 100});
	this.parameter('width', {value: 360});
	this.parameter('height', {value: 480});
	this.parameter('precision', {value: 10});
	this.output('movement');
}

var last_move_ts = new Date();
var network_time = 500;

function tagsReaction() {
	var width = this.getParameter('width');
	var height = this.getParameter('height');
	var precision = this.getParameter('precision');;
	var speed = this.getParameter('speed');

	var tags = this.get('tags');

	var x = (width/2) - tags[0].center[0];
	var y = (height/2) - tags[0].center[1];

	var absX = Math.abs(x);
	var absY = Math.abs(y);
	console.log("April is " + ((x > 0) ? "right" : "left") + " by " + absX + "px");

	var pan = Math.log(precision*absX)*(x/absX);
	var tilt = Math.log(precision*absY)*(y/absY);
	if ((new Date()-last_move_ts) > network_time) {
		this.send('movement', {pan: pan, panSpeed: speed, tilt: tilt, tiltSpeed: speed});
		console.log("Movement sent" + pan);
		last_move_ts = new Date();
	}
}

exports.initialize = function () {
	this.addInputHandler('tags', tagsReaction);
}

