/*jshint globalstrict: true*/
"use strict";

var http = require('httpClient');

// Node Host needs util defined.
var util = require('util');

/** Inputs and outputs definition **/
exports.setup = function() {
    this.input('command');
    this.parameter('kubiLocalServerIP', {
        type: "string",
        value: ""
    });
    this.parameter('kubiLocalServerPort', {
        type: "string",
        value: "8080"
    });
    this.parameter('incremental', {type: 'boolean', value: true});
    this.output('urlRequested', {
        type: "string"
    });
};

function Kubi() {
    var kubi = {};

    kubi.lastUrlRequested = "";

    // Private variables
    var debug = true;
    var ipAddress = "";
    var port = "";
    var path = "";
    var url = "";
    var connected = false;

    // Use self in contained functions so the caller does not have to bind "this"
    // on each function call.
    var self = this;

    //Private functions.

     /**
     *  Parse command to create a command object
     */
     function parseCommand(command) {
        var commandObject = {};

        if (typeof command === 'string') {
            command = JSON.parse(command);
        }

        // Check whether input is valid
        if (Object.keys(command).length < 1) {
            self.error("Command is empty: " + command);
        } else {
            // Get interresting properties
            if (typeof command.pan !== 'undefined') {
                commandObject.pan = command.pan;
            }
            if (typeof command.panDelta !== 'undefined') {
                commandObject.pan = command.panDelta;
            }
            if (typeof command.tilt !== 'undefined') {
                commandObject.tilt = command.tilt;
            }
            if ( typeof command.tiltDelta !== 'undefined') {
                commandObject.tilt = command.tiltDelta;
            }
            if (typeof command.tiltSpeed !== 'undefined') {
                commandObject.tiltSpeed = command.tiltSpeed;
            }
            if (typeof command.panSpeed !== 'undefined') {
                commandObject.panSpeed = command.panSpeed;
            }
            //FIXME: Use commandObject.pan = command.pan || command.panDelta || 0; instead?
        }

        return commandObject;
     }

     /**
     *  Build URL to request with commands
     */
     function buildGetUrl(incremental, correctCommand) {
        var requestUrl = url;
        var panVar, tiltVar;
        var panSpeedVar = "panSpeed";
        var tiltSpeedVar = "tiltSpeed";

        console.log("Building URL...");

        if (incremental == true) {
            panVar = "panDelta";
            tiltVar = "tiltDelta";
        } else {
            panVar = "pan";
            tiltVar = "tilt";
        }
        requestUrl += "?" + panVar + "=" + correctCommand.pan + "&"
                                             + panSpeedVar + "=" + correctCommand.panSpeed + "&"
                                             + tiltVar + "=" + correctCommand.tilt + "&"
                                             + tiltSpeedVar + "=" + correctCommand.tiltSpeed;
        return requestUrl;
     }

     /**
     *  Report all server errors if they exist and return true in this case.
     */
     function reportIfError(response) {
        var body = response.body;
        //FIXME: Server not ready to give json...
        return true; //Remove this line after server update

        if (typeof body == "string") {
            body = JSON.parse(body);
        }

        if (body instanceof Array && obj.length > 0 && body[0].error) {
            self.error("Server error report: " + error.description);
            return true;
        }
        return false;
     }

    // Public functions.

    kubi.connect = function() {
        if (debug) {
            console.log("Kubi.js: Connecting...");
        }
        ipAddress = self.getParameter('kubiLocalServerIP');
        port = self.getParameter('kubiLocalServerPort') || "8080";
        path = self.getParameter('incremental') ? "/incremental" : "/absolute";

        if (ipAddress === null || ipAddress.trim() === "") {
            throw "No IP Address is given for the Kubi server.";
        }

        url = "http://" + ipAddress + ":" + port + path;

        connected = true;
        //TODO: Implement token auth on server side
     };

     /**
     *  Issue a command to the server.
     */
     kubi.issueCommand = function () {
        var command = self.get('command');
        if (debug) {
            console.log("Kubi.js: issueCommand(): " + util.inspect(command));
        }
         //TODO: check authentification, queue command until server authentification

         kubi.processCommand(command);
     };

     /**
     *  Process received command after authentification and connectivity checks.
     */
     kubi.processCommand = function (command) {
        console.log("Proccessing command");
        var commandObject = parseCommand(command);
        console.log("Got command object ");
        if (typeof commandObject !== 'undefined') {
            var requestUrl = buildGetUrl(self.getParameter('incremental'), commandObject);

            http.get(requestUrl, function(response) {
            	if (response !== null) {
	                if (false) {
	                    console.log("Kubi.js: processCommands(): response status: " + response.statusMessage);
	                    console.log("Kubi.js: processCommands(): response body: " + response.body);
	                }
	                kubi.lastUrlRequested = requestUrl;
	                reportIfError(response);
            	} else {
            		self.error("Unable to connect to the server on " + requestUrl);
            	}

            	self.send('urlRequested', requestUrl);
            });

            return;
        } else {
            console.log("commandObject undefined");
        }
     };


     return kubi;
}

exports.initialize = function() {
    this.kubi = Kubi.call(this);

    console.log("Kubi.js: Initializing...");
    this.addInputHandler('command', this.kubi.issueCommand);
    console.log("Kubi.js: Handler added");
    this.kubi.connect();
};

