// Put your JavaScript program here.
// Add ports and parameters.
// Define JavaScript functions initialize(), fire(), and/or wrapup().
// Refer to parameters in scope using dollar-sign{parameterName}.
// In the fire() function, use get(parameterName, channel) to read inputs.
// Send to output ports using send(value, portName, channel).

exports.setup = function() {
	var self = this;

	this.input('input');
	this.output('output');

	this.addInputHandler('input', function() {
		var text = self.get('input');
		var host = "mary.dfki.de";
		// var host = "localhost";

		// Globally replace whitespace with + signs.
		text = text.replace(/\s/g, '+');
		var url = "http://" + host + ":59125/process?INPUT_TEXT=" + text + "&INPUT_TYPE=TEXT&OUTPUT_TYPE=AUDIO&AUDIO=AIFF_FILE&LOCALE=en_US";
	    self.send('output', url);
	});
}

