var name;

exports.initialize = function() {
  name = null;
  send("Please type your name. Type quit to exit.", feedback);
}

var handleInput = function() {
  var read = get(input);
  send(read, feedback);
  if (read == 'quit' || read == 'exit') {
    if (name) {
      send(name + ' has left.', output);
    }
    send(true, stop);
   } else  if (!name) {
    name = read;
    send(name + ' has joined.', output);
    first = false;
  } else {
    send(name + ': ' + read, output);
  }
}

addInputHandler(handleInput, input);

