/** Test accessor that spontaneously produces outputs once per time interval.
 *  This implementation produces a counting sequence.
 *
 *  @accessor test/TestSpontaneous
 *  @parameter interval The interval between outputs in milliseconds.
 *  @output output Output for the counting sequence, of type number.
 *  @author Edward A. Lee
 *  @version $$Id: TestSpontaneous.js 546 2016-02-03 02:07:57Z cxh $$
 */

exports.setup = function() {
    this.parameter('interval', {'type':'number', 'value':1000});
    this.output('output', {'type': 'number'});
};

// These variables will not be visible to subclasses.
var handle = null;
var count = 0;

exports.initialize = function() {
    count = 0;
    // Need to record 'this' for use in the callback.
    var thiz = this;
    handle = setInterval(function() {
        thiz.send('output', count++);
    }, this.getParameter('interval'));
};

exports.wrapup = function() {
    if (handle) {
        clearInterval(handle);
        handle = null;
    }
};

/** Output a sequence with a given step in values.
 *
 *  @accessor JavaScriptRamp
 *  @param init The value produced on its first iteration.  The
 *  initial default is 0.
 *  @input trigger The trigger
 *  @output output The output
 *  @param step The amount by which the output is incremented. The
 *  default is 1.
 *  @author Christopher Brooks
 *  @version $$Id: GDPLogRead.xml 75037 2016-08-11 21:29:55Z cxh $$
 */
exports.setup = function() {
    // FIXME: this only supports numbers, unlike the Cape Code Ramp
    // actor, which supports many types.
    this.parameter('init', {'type':'number', 'value':0});
    this.input('trigger');
    this.output('output', {'type':'number'});
    this.parameter('step', {'type':'number', 'value':1});
};

var _lastValue = 0;

exports.initialize = function() {
    _lastValue = this.getParameter('init');
}
exports.fire = function() {
    _lastValue += this.getParameter('step');
    this.send('output', _lastValue);
};

