/** Accessor that spontaneously produces outputs once per time interval.
 *  This implementation produces a counting sequence.
 *
 *  @accessor test/Clock
 *  @parameter interval The interval between outputs in milliseconds.
 *  @output output Output for the counting sequence, of type number.
 *  @author Edward A. Lee
 *  @version $$Id$$
 */

// Stop extra messages from jslint.  Note that there should be no
// space between the / and the * and global.
/*globals clearInterval, exports, require, setInterval */
/*jshint globalstrict: true*/
"use strict";

exports.setup = function () {
    this.parameter('interval', {
        'type': 'number',
        'value': 1000
    });
    this.output('output', {
        'type': 'number'
    });
};

// These variables will not be visible to subclasses.
var handle = null;
var count = 0;

exports.initialize = function () {
    count = 0;
    // Need to record 'this' for use in the callback.
    var thiz = this;

    // Send an output and then call setInterval
    thiz.send('output', count);
    count += 1;

    handle = setInterval(function () {
        thiz.send('output', count);
        count += 1;
    }, this.getParameter('interval'));
};

exports.wrapup = function () {
    if (handle) {
        clearInterval(handle);
        handle = null;
    }
};

/*global console, error, exports, readURL */
/*jshint globalstrict: true */
"use strict";

exports.setup = function () {
 	this.output('key', {
        'type': 'string',
    });
    this.output('value', {
        'type': 'string'
    });
    this.input('trigger');
}

var outputs = [ ["a","apple"], ["b", "bed"], ["c", "car"], ["d", "dog"], ["e", "eagle"], ["a", "ace"], ["c",""], ["d", ""], ["e",""], ["a",""] ];
var count;
exports.initialize = function() {
	count = 0
    this.addInputHandler('trigger', handleInputs.bind(this));
}

function handleInputs() {
	this.send('value', outputs[count][1]);
	this.send('key', outputs[count][0]);

	count = count + 1;
	if (count >= outputs.length) {
		count = 0;
	}
}

/*global console, error, exports, readURL */
/*jshint globalstrict: true */
"use strict";

exports.setup = function () {
 	this.output('output', {
        'type': 'string',
    });
    this.input('input');
}

exports.initialize = function() {
    this.addInputHandler('input', handleInputs.bind(this));
}

function handleInputs() {
	var inputValue = this.get('input');
	this.send('output', inputValue.replace(/directory of \/tmp\/.*/, 'directory of /tmp/XXXX'));
}
