var mapManager = require('mapManager');

exports.initialize = function(){

	var myCoords = new mapManager.CoordinateSystem("myCoordsName");
	var myMap = new mapManager.Map("myMapName" , mapManager.SpaceTypeEnum.EUCLIDEAN, myCoords);
	mapManager.registerMap(myMap);


	var cat = new mapManager.Entity("cat");
	var bird = new mapManager.Entity("bird");
	var dog = new mapManager.Entity("dog");

	send("output","Global entities (should be empty)");
	send("output", mapManager.entitiesToString());

	send("output", "Registering cat (should be true)");
	send("output", mapManager.registerEntity(cat));

	send("output", "Registering cat again (should be false)");
	send("output", mapManager.registerEntity(cat));
	send("output", "Registering dog (should be true)");
	send("output", mapManager.registerEntity(dog));
	send("output", "Registering bird (should be true)");
	send("output", mapManager.registerEntity(bird));

	send("output", "Global entities (should have cat, dog, and bird)");
	send("output", mapManager.entitiesToString());


	send("output", "myMap's mapEntities.");
	send("output", myMap.mapEntitiesToString());

	send("output", "Adding cat to myMap (should be true)");
	send("output", myMap.addEntity(cat));
	send("output", "Adding cat to myMap again (should be false)");
	send("output", myMap.addEntity(cat));
	send("output", "Adding dog to myMap (should be true)");
	send("output", myMap.addEntity(dog));

	send("output", "myMap's mapEntities.");
	send("output", myMap.mapEntitiesToString());
	send("output", "Global entities");
	send("output", mapManager.entitiesToString());

	send("output", "Removing dog. Should be true.");
	send("output", myMap.removeEntity(dog));
	send("output", "Removing dog again. Should be false.");
	send("output", myMap.removeEntity(dog));

	send("output", "myMap's mapEntities.");
	send("output", myMap.mapEntitiesToString());
	send("output", "Global entities");
	send("output", mapManager.entitiesToString());

	var unregisteredThing = new mapManager.Entity("unregisteredThing");
	send("output", "Attempting to add an unregistered entity to myMap");
	try{
		myMap.addEntity(unregisteredThing);
		send("output", "Test failure, this line should not execute :-(");
	} catch(e){
		send("output", e);
	}

	send("output", "Attempting to remove an unregistered entity from myMap");
	try{
		myMap.removeEntity(unregisteredThing);
		send("output", "Test failure, this line should not execute :-(");
	} catch(e){
		send("output", e);
	}

