exports.initialize = function(){

var mapManager = require('mapManager');


//The names in this test seem to be more ontological than entity alias based,
//but it's just a test of the mechanism.

var cat = new mapManager.Entity("cat");
var mammal = new mapManager.Entity("mammal");
var animals = [
	cat,
	new mapManager.Entity("bird"),
	new mapManager.Entity("dog"),
	new mapManager.Entity("fish"),
	new mapManager.Entity("walrus"),
	new mapManager.Entity("salmon"),
	mammal
];

for( var i = 0; i < animals.length; i++){
	mapManager.registerEntity(animals[i]);
}


send("output", "Global Entities:");
send("output", mapManager.entitiesToString());

var zebra = new mapManager.Entity("zebra")
send("output", "Zebra is an unregistered entity");

send("output", "Attempting to alias cat as itself (should print false)");
send("output", cat.addAlias(cat));

send("output", "Attempting to alias cat as mammal (should print true)");
send("output", cat.addAlias(mammal));

send("output", "Attempting to alias cat as mammal again (should print false)");
send("output", cat.addAlias(mammal));

send("output", "Printing cat's aliases (should just be mammal)");
send("output", cat.aliasesToString());

send("output", "Giving mammal aliases with cat, bird, dog, fish, walrus, salmon, and mammal.");
for( var i = 0; i < animals.length; i++){
	mammal.addAlias(animals[i]);
}

send("output", mammal.aliasesToString());

send("output", "Printing global repo as JSON");
send("output", mapManager.localRepoToJSONString());

//var myCoords = new mapManager.CoordinateSystem("myCoords")
//var myMap = new mapManager.Map( "myMapName", MapManager.SpaceTypeEnum.Euclidean , myCoords); 


}
