exports.initialize = function(){

var mapManager = require('mapManager');

var myCoords = new mapManager.CoordinateSystem("Joey");
var yourCoords = new mapManager.CoordinateSystem("Sally");
var myMap = new mapManager.Map("myMapName" , mapManager.SpaceTypeEnum.EUCLIDEAN, myCoords);
var yourMap = new mapManager.Map("yourMapName", mapManager.SpaceTypeEnum.TOPOLOGICAL, yourCoords);
var badMap = new mapManager.Map("badMapName", mapManager.SpaceTypeEnum.EUCLIDEAN, myCoords);

send("output", "Registering myMap, should print true");
send("output", mapManager.registerMap(myMap));
send("output", "Registering myMap again, should print false");
send("output", mapManager.registerMap(myMap));

send("output", "Registering yourMap, should print true");
send("output", mapManager.registerMap(yourMap));

send("output", "Printing maps. badMap is not registered, should not be printed.");
send("output", mapManager.mapsToString());

send("output", "Creating and registering sampleEntity");
var sampleEntity = new mapManager.Entity("sampleEntity");
mapManager.registerEntity(sampleEntity);

send("output", "Attempting to add sampleEntity to myMap (should print true)");
send("output",  myMap.addEntity(sampleEntity));

send("output", "Attempting to add sampleEntity to badMap (should print exception)");

try{
	badMap.addEntity(sampleEntity);
	send("output", "Test failure, this line should not execute :-(");
} catch(e){
	send("output", e);
}

send("output", "Attempting to remove sampleEntity from myMap (should print true)");
send("output", myMap.removeEntity(sampleEntity));

send("output", "Attempting to remove sampleEntity from badMap (should print exception)");
try{
	badMap.removeEntity(sampleEntity);
	send("output", "Test failure, this line should not execute :-(");
} catch(e){
	send("output",  e );
}


send("output", "Printing contents of local repo as JSON");
var repo =  mapManager.localRepoToJSONString();
send("output",repo);
}
