exports.setup = function() {
	this.parameter('interval',
		{type : 'number'}
	);

	this.output('gesture', 
		{type : 'string'}
	);

	this.gestures = ['up', 'down', 'rotate'];
};

exports.initialize = function() {
	var i = 0;
	var self = this;
	setInterval(function() {
		self.send('gesture', self.gestures[i % self.gestures.length]);
		i++;
	}, self.getParameter('interval'));
};

