/*globals console, error, exports, require */
/*jshint globalstrict: true*/
"use strict";

var util = require('util');

exports.setup = function () {
    this.input('input');
    this.output('output');
};

exports.initialize = function () {
    this.addInputHandler('input', function () {
        var inputValue = this.get('input');
        console.log(util.inspect(inputValue));
        this.send('output', inputValue);
    });
};

// Put your JavaScript program here.
// Add ports and parameters.
// Define JavaScript functions initialize(), fire(), and/or wrapup().
// Refer to parameters in scope using dollar-sign{parameterName}.
// In the fire() function, use get(parameterName, channel) to read inputs.
// Send to output ports using send(value, portName, channel).

var util = require('util');

exports.setup = function() {
	this.input('gesture', 
		{type : 'string'}
		);

	this.input('newCommand',
		{type : 'string'}
		);

	this.output('log');
	this.output('command');

	this.parameter('waitTime', 
		{ 'type' : 'number',
		  'value' : 6 }
	);

 	this.gestureMap = 
 		{ 'up' : 'next', 'down' : 'previous', 'rotate' : 'beginning' };

	this.commandHandler = null;
    this.gestureHandler = null;

    this.pendingCommand = null;
    this.pendingGesture = null;
};

exports.initialize = function() {
	var self = this;

	// New commands.
	this.addInputHandler('newCommand', function() {
		self.pendingCommand = self.get('newCommand');

		// Check for waiting gesture.  If one, remap command and issue output.
		if (self.gestureHandler !== null) {
			self.send('log', 'Remapping gesture "' + self.pendingGesture + '" to command "' + self.pendingCommand + '"');
			self.gestureMap[self.pendingGesture] = self.pendingCommand;
			self.send('command', self.gestureMap[self.pendingGesture]);
			self.pendingCommand = null;
			self.pendingGesture = null;
			clearTimeout(self.commandHandler);
			clearTimeout(self.gestureHandler);
			self.commandHandler = null;
			self.gestureHandler = null;
		}

		// If none, wait for the specified wait time.
		// Overwrite any previous commands.
		if (self.commandHandler !== null) {
			clearTimeout(self.commandHandler);
		}
		self.commandHandler = setTimeout(function() {
			self.commandHandler = null;
			}, self.getParameter('waitTime'));
	});

	// New gesture.
	this.addInputHandler('gesture', function() {

		// Output commands for any previous gestures.
		if (self.pendingGesture !== null) {
			if (self.gestureMap[self.pendingGesture] !== null && 
			     typeof self.gestureMap[self.pendingGesture] !== 'undefined') {
			     	self.send('command', self.gestureMap[self.pendingGesture]);
			     }
			self.pendingGesture = null;
			clearTimeout(self.gestureHandler);
		}

		self.pendingGesture = self.get('gesture');

		// Check for pending commands.  If one, remap and output the new command.
		if (self.commandHandler !== null) {
			self.gestureMap[self.pendingGesture] = self.pendingCommand;
			clearTimeout(self.commandHandler);
			clearTimeout(self.gestureHandler);
			self.send('log', 'Remapping gesture "' + self.pendingGesture + '" to command "' + self.pendingCommand + '"');
			self.send('command', self.gestureMap[self.pendingGesture]);
			self.pendingCommand = null;
			self.pendingGesture = null;
			self.commandHandler = null;
			self.gestureHandler = null;
		} else {
			// If no pending commands, wait for a fixed time for any new commands.
			// If this causes too much delay, we may want to immediately 
			// output a command for the gesture, then wait for any remappings 
			// and process them.  Or we could require that the command be spoken
			// before the gesture so that the command will be waiting. 
			self.gestureHandler = setTimeout(function() {
				if (self.gestureMap[self.pendingGesture] !== null && 
					self.gestureMap[self.pendingGesture] !== 'undefined') {
						self.send('command', self.gestureMap[self.pendingGesture]);
				} else {
					self.send('log', 'No command for gesture ' + self.pendingGesture);
				}
				self.pendingGesture = null;
				self.gestureHandler = null;	
			}, self.getParameter('waitTime'));
		}
	});
};

