// Define a few helper functions to help set up the watch data
/** Convert short int to 2 bytes array.                                                                          
 */
function short_to_bytes(n) {
    var b = new Uint8Array([0, 0]);
    if (b < 0) {
        n = (Math.pow(2, 16) + n);
    }
    b[0] = n & 0xFF;
    n >>= 8;
    b[1] = n & 0xFF;
    return b;
}

/** Convert int to 3 bytes array. */
function int_to_3bytes(n) {
    var b = new Uint8Array([0, 0, 0]);
    b[0] = n & 0xFF;
    n >>= 8;
    b[1] = n & 0xFF;
    n >>= 8;
    b[2] = n & 0xFF;
    return b;
}

// Convert int to 6 bytes array.                                                                                 
function timestamp_2_bytes(n) {
    var b = new Uint8Array([0, 0, 0, 0, 0, 0]);
    var milisce = n % 1000;
    n /= 1000;
    b[0] = n & 0xFF;
    n >>= 8;
    b[1] = n & 0xFF;
    n >>= 8;
    b[2] = n & 0xFF;
    n >>= 8;
    b[3] = n & 0xFF;
    b[4] = milisce & 0xFF;
    milisce >>= 8;
    b[5] = milisce & 0xFF;
    return b;
}

/** Create an unsigned byte array with the watch data.
 *  @return The unsigned byte array.
 */
function initializeWatchData() {

    // package head info -- Device data and Package type                                                         
    var DEV_ID = '9096';
    var WATCH_TYPE = "w".charCodeAt(0);
    var GLASS_TYPE = "g".charCodeAt(0);
    var BATTERY_TYPE = "b".charCodeAt(0);
    var ACCELEROMETER_TYPE = "A".charCodeAt(0);
    var GYRO_TYPE = "G".charCodeAt(0);

    // Original data of the data body for all three kinds of package.                                            
    var ACCx = 0.98;
    var ACCy = 0.01;
    var ACCz = -0.02;
    var GYRx = 0.2;
    var GYRy = -0.3;
    var GYRz = 0.4;
    var PPG = 100000;
    var HR = 70;
    var BATTERY_LIFE = 90;

    // Convert the original data to the format the data body requires.                                           
    var accx = (ACCx * 10000) | 0;
    var accy = (ACCy * 10000) | 0;
    var accz = (ACCz * 10000) | 0;
    var gyrx = (GYRx * 10000) | 0;
    var gyry = (GYRy * 10000) | 0;
    var gyrz = (GYRz * 10000) | 0;

    // Length of accelerometer packets.
    var aLength = 17;
    // Length of gyroscope packets.
    var gLength = 17;

    // Send 5 ac            
    var watch_p = new Uint8Array(10 * (aLength + gLength));

    watch_p[4] = ACCELEROMETER_TYPE;
    for(var i = 0; i < 10; i++) {
        var now = Date.now();
        var offset = i * (aLength + gLength);
        var offset2 = offset + aLength;

        watch_p[0 + offset] = DEV_ID.charCodeAt(0);
        watch_p[1 + offset] = DEV_ID.charCodeAt(1);
        watch_p[2 + offset] = DEV_ID.charCodeAt(2);
        watch_p[3 + offset] = DEV_ID.charCodeAt(3);

	    watch_p[4 + offset] = ACCELEROMETER_TYPE;

        watch_p[5 + offset] = short_to_bytes(accx)[0];
        watch_p[6 + offset] = short_to_bytes(accx)[1];
        watch_p[7 + offset] = short_to_bytes(accy)[0];
        watch_p[8 + offset] = short_to_bytes(accy)[1];
        watch_p[9 + offset] = short_to_bytes(accz)[0];
        watch_p[10 + offset] = short_to_bytes(accz)[1];

        watch_p[11 + offset] = timestamp_2_bytes(now)[0];
        watch_p[12 + offset] = timestamp_2_bytes(now)[1];
        watch_p[13 + offset] = timestamp_2_bytes(now)[2];
        watch_p[14 + offset] = timestamp_2_bytes(now)[3];
        watch_p[15 + offset] = timestamp_2_bytes(now)[4];
        watch_p[16 + offset] = timestamp_2_bytes(now)[5];

        watch_p[0 + offset2] = DEV_ID.charCodeAt(0);
        watch_p[1 + offset2] = DEV_ID.charCodeAt(1);
        watch_p[2 + offset2] = DEV_ID.charCodeAt(2);
        watch_p[3 + offset2] = DEV_ID.charCodeAt(3);

	    watch_p[4 + offset2] = GYRO_TYPE;

        watch_p[5 + offset2] = short_to_bytes(gyrx)[0];
        watch_p[6 + offset2] = short_to_bytes(gyrx)[1];
        watch_p[7 + offset2] = short_to_bytes(gyry)[0];
        watch_p[8 + offset2] = short_to_bytes(gyry)[1];
        watch_p[9 + offset2] = short_to_bytes(gyrz)[0];
        watch_p[10 + offset2] = short_to_bytes(gyrz)[1];

        watch_p[11 + offset2] = timestamp_2_bytes(now)[0];
        watch_p[12 + offset2] = timestamp_2_bytes(now)[1];
        watch_p[13 + offset2] = timestamp_2_bytes(now)[2];
        watch_p[14 + offset2] = timestamp_2_bytes(now)[3];
        watch_p[15 + offset2] = timestamp_2_bytes(now)[4];
        watch_p[16 + offset2] = timestamp_2_bytes(now)[5];

        //watch_p[5 + i * 22 + 12] = int_to_3bytes(PPG)[0];
        //watch_p[5 + i * 22 + 13] = int_to_3bytes(PPG)[1];
        //watch_p[5 + i * 22 + 14] = int_to_3bytes(PPG)[2];
        //watch_p[5 + i * 22 + 15] = (HR & 0xFF);
    }
    return watch_p;
}

// These variables will not be visible to subclasses.
var handle = null;
var count = 0;

exports.generate = function() {
	var watch_p = initializeWatchData();
	this.send('output', watch_p);
}

exports.initialize = function() {
	handle = this.addInputHandler('trigger', this.exports.generate.bind(this));
};

exports.wrapup = function() {
    if (handle !== null) {
        //clearInterval(handle);
        //handle = null;
        this.removeInputHandler(handle);
    }
};

