exports.setup = function() {
  this.input('connectionReady');
  this.output('toSend');
}

var handle;
exports.initialize  = function() {
  handle = this.addInputHandler('connectionReady', handler.bind(this));
}

function handler() {
  this.send('toSend', '0123456789');
}

exports.wrapup = function() {
  this.removeInputHandler(handle);
}

