/** Output sensor data.
 *  FIXME: explain the sensor data.
 *  E.g.,
 *    - distance: An integer giving the total accumulated travel distance.
 *    - angle: 
 */   
/***preinitBlock***/
// Global variables
volatile uint16_t timer_cnt = 0;
volatile uint8_t timer_on = 0;
volatile uint8_t sensors_flag = 0;
volatile uint8_t sensors_index = 0;
volatile uint8_t sensors_in[Sen0Size];
volatile uint8_t sensors[Sen0Size];
volatile int16_t distance = 0;
volatile int16_t angle = 0;

// Delay for the specified time in ms and update sensor values
// during the delay by copying data from the interrupt service
// routine buffer.
void delayAndUpdateSensors(uint16_t time_ms) {
  uint8_t temp;
  // Start timer counter (which will be decremented
  // by the timer interrupt), and while the timer is
  // running, if a new batch of sensor data is completed
  // (by the serial port interrupt service routine),
  // then update the sensor data.
  timer_on = 1;
  timer_cnt = time_ms;
  while(timer_on) {
    // The sensors_flag is set to zero initially,
    // and when a complete set of sensor data has been read.
    if(!sensors_flag) {
      for(temp = 0; temp < Sen6Size; temp++) {
        sensors[temp] = sensors_in[temp];
      }

      // Update running totals of distance and angle
      distance += (int)((sensors[SenDist1] << 8) | sensors[SenDist0]);
      angle += (int)((sensors[SenAng1] << 8) | sensors[SenAng0]);

      // Issue a command to read the sensors again.
      byteTx(CmdSensors);
      // Request packet 0, which has 26 bytes of information.
      byteTx(0);
      sensors_index = 0;
      // Set a flag for the interrupt service routine to
      // know that we have issued the CmdSensors command to
      // read the sensors.
      sensors_flag = 1;
    }
  }
}

// Serial receive interrupt to store sensor values.
// This is called each time a byte arrives on the serial port.
SIGNAL(SIG_USART_RECV) {
  uint8_t temp;
  // Read the register that contains the received byte.
  temp = UDR0;
  // The delayAndUpdateSensors() procedure sets
  // sensors_flag to 1 after sending a command to the
  // iRobot Create to send back all the sensor data.
  // Thus, if that flag has been set, then the incoming
  // data must be sensor data.
  if(sensors_flag) {
    sensors_in[sensors_index++] = temp;
    if(sensors_index >= Sen6Size) {
      // Indicate that we have read a complete set.
      sensors_flag = 0;
    }
  }
}

// Timer 1 interrupt to time delays in ms
SIGNAL(SIG_OUTPUT_COMPARE1A) {
  // If a timer has been set using delayMs,
  // then decrement the timer count until
  // it reaches zero, and then turn the timer off.
  if(timer_cnt) {
    timer_cnt--;
  } else {
    timer_on = 0;
  }
}
/**/

/***initBlock***/
  // Set up timer 1 to generate an interrupt every 1 ms.
  TCCR1A = 0x00;
  TCCR1B = (_BV(WGM12) | _BV(CS12));
  OCR1A = 71;
  TIMSK1 = _BV(OCIE1A);

  // Set up the serial port with rx interrupt.
  UBRR0 = 19;
  UCSR0B = (_BV(RXCIE0) | _BV(TXEN0) | _BV(RXEN0));
  UCSR0C = (_BV(UCSZ00) | _BV(UCSZ01));

  // Set the sensor data to be all zero.
  // This initializes the buffer that gets
  // filled by the interrupt service routine that
  // reads from the serial port.
  for(int i = 0; i < Sen0Size; i++) {
     sensors_in[i] = 0x0;
  }

  // Turn on interrupts.
  sei();

  // Delay the amount of time given by the pollingPeriodInMs
  // parameter, and during that time, update the sensor data.
  // The very first update of the sensor data will
  // initialize all sensor data to zero.
  delayAndUpdateSensors($val(pollingPeriodInMs));
/**/

/***fireBlock***/
  // Delay the amount of time given by the pollingPeriodInMs
  // parameter, and during that time, update the sensor data.
  delayAndUpdateSensors($val(pollingPeriodInMs));
  // Output aggregated data for wheel drops and bumps.
  $ref(wheelDrop) = sensors[SenBumpDrop] & WheelDropAll;            
  $ref(bump) = sensors[SenBumpDrop] & BumpEither; 
  $ref(wall) = sensors[SenWall];
  $ref(cliffLeft) = sensors[SenCliffL];
  $ref(cliffFrontLeft) = sensors[SenCliffFL];
  $ref(cliffFrontRight) = sensors[SenCliffFR];
  $ref(cliffRight) = sensors[SenCliffR];
  $ref(virtualWall) = sensors[SenVWall];
  $ref(advanceButton) = sensors[SenButton] & ButtonAdvance;
  $ref(playButton) = sensors[SenButton] & ButtonPlay;
  // Update accumulated distance and angle.
  distance += (int)((sensors[SenDist1] << 8) | sensors[SenDist0]);
  $ref(distance) = distance;
  angle += (int)((sensors[SenAng1] << 8) | sensors[SenAng0]);
  $ref(angle) = angle;
/* FIXME: Skipped the battery state
#define SenChargeState  16
#define SenVolt1        17
#define SenVolt0        18
#define SenCurr1        19
#define SenCurr0        20
#define SenTemp         21
#define SenCharge1      22
#define SenCharge0      23
#define SenCap1         24
#define SenCap0         25
*/

