/** Send a command to the iRobot Create to drive at
 *  the specified velocity, in mm per second, and at the
 *  specified angle, in mm.  The velocity can range from
 *  -500 to 500, and the radius from -2000 to 2000.

FIXME: Use portParameters.
FIXME: ExpertParameter STRAIGHT, etc.
FIXME: Details from the manual below:

The longer radii make Create drive straighter, while
the shorter radii make Create turn more. The radius is
measured from the center of the turning circle to the center
of Create. A Drive command with a positive velocity and a
positive radius makes Create drive forward while turning
toward the left. A negative radius makes Create turn toward
the right. Special cases for the radius make Create turn
in place or drive straight, as specified below. A negative
velocity makes Create drive backward.
NOTE: Internal and environmental restrictions may prevent
Create from accurately carrying out some drive commands.
For example, it may not be possible for Create to drive at
full speed in an arc with a large radius of curvature.
 Serial sequence: [137] [Velocity high byte] [Velocity low byte]
[Radius high byte] [Radius low byte]
 Available in modes: Safe or Full
 Changes mode to: No Change
 Drive data byte 1: Velocity (-500  500 mm/s)
 Drive data byte 2: Radius (-2000  2000 mm)
Special cases:
Straight = 32768 or 32767 = hex 8000 or 7FFF
Turn in place clockwise = hex FFFF
Turn in place counter-clockwise = hex 0001

 */

/***preinitBlock***/
// Send Create drive commands in terms of velocity and radius
#ifndef _DRIVE_DRIVE
#define _DRIVE_DRIVE
void drive(int16_t velocity, int16_t radius)
{
  byteTx(CmdDrive);
  byteTx((uint8_t)((velocity >> 8) & 0x00FF));
  byteTx((uint8_t)(velocity & 0x00FF));
  byteTx((uint8_t)((radius >> 8) & 0x00FF));
  byteTx((uint8_t)(radius & 0x00FF));
}
#endif
/**/

/***initBlock***/
  // As a precaution, stop driving.
  // RadStraight = 32768, the code for no turns.
  drive(0, RadStraight);
/**/

/***fireBlock***/
  drive($ref(velocity), $ref(radius));
/**/

/***wrapupBlock***/
/**/

// Turn on or off the LEDs on the iRobot Create
// and the command module.  If the toggle parameter is false
// (the default), then if the corresponding input
// is true, the LED is turned on, and otherwise it
// is turned off. If the toggle parameter
// is true, then each time a true input is received
// on the relevant port after a false has been received
// since the last true, the corresponding LED state
// is reversed (if it is on, it goes off, and if it is
// off, it goes on). This actor always produces a true
// value on its output; the output is useful to ensure
// that downstream actors execute after this one.

/***preinitBlock***/
  // Start in a state that assumes a false has been received
  // And each LED is off. These variables are not needed
  // if toggle is false.
  #if $val(toggle)
  uint8_t $actorSymbol(advanceFalseReceived) = 0x01;
  uint8_t $actorSymbol(playFalseReceived) = 0x01;
  uint8_t $actorSymbol(LED1FalseReceived) = 0x01;
  uint8_t $actorSymbol(LED2FalseReceived) = 0x01;
  #endif
  uint8_t $actorSymbol(LEDCommand) = 0x00;
/**/

/***initBlock***/
/**/

/***fireBlock***/
    // First handle the input for the iRobot Create Advance LED.
    if($ref(advanceLED)) {
      // True input received.
      // Note use of preprocessor for conditional code.
      #if ($val(toggle))
        // If the input differs from what it was
        // on the previous firing and is true, then
        // toggle the state of the LED.
        if ($actorSymbol(advanceFalseReceived)) {
          // First true since the last false. Toggle.
          if ($actorSymbol(LEDCommand) & LEDAdvance) {
            // LED is ON. Turn it off.
            $actorSymbol(LEDCommand) &= ~LEDAdvance;
          } else {
            // LED is OFF. Turn it on.
            $actorSymbol(LEDCommand) |= LEDAdvance;
          }
        }
        // Indicate that a false has NOT been received.
        $actorSymbol(advanceFalseReceived) = 0;
      #else
        // toggle is false.
        // Just turn on the LED.
        $actorSymbol(LEDCommand) |= LEDAdvance;
      #endif
    } else {
      // False input is received.
      // If toggle is false, then turn the LED off.
      // Note use of preprocessor for conditional code.
      #if ($val(toggle))
        // Indicate that a false has been received.
        $actorSymbol(advanceFalseReceived) = 1;
      #else
        // Just turn off the LED.
        $actorSymbol(LEDCommand) &= ~LEDAdvance;
      #endif
    }
    // Next handle the input for the iRobot Create Play LED.
    if($ref(playLED)) {
      // True input received.
      // Note use of preprocessor for conditional code.
      #if ($val(toggle))
        // If the input differs from what it was
        // on the previous firing and is true, then
        // toggle the state of the LED.
        if ($actorSymbol(playFalseReceived)) {
          // First true since the last false. Toggle.
          if ($actorSymbol(LEDCommand) & LEDPlay) {
            // LED is ON. Turn it off.
            $actorSymbol(LEDCommand) &= ~LEDPlay;
          } else {
            // LED is OFF. Turn it on.
            $actorSymbol(LEDCommand) |= LEDPlay;
          }
        }
        // Indicate that a false has NOT been received.
        $actorSymbol(playFalseReceived) = 0;
      #else
        // toggle is false.
        // Just turn on the LED.
        $actorSymbol(LEDCommand) |= LEDPlay;
      #endif
    } else {
      // False input is received.
      // If toggle is false, then turn the LED off.
      // Note use of preprocessor for conditional code.
      #if ($val(toggle))
        // Indicate that a false has been received.
        $actorSymbol(playFalseReceived) = 1;
      #else
        // Just turn off the LED.
        $actorSymbol(LEDCommand) &= ~LEDPlay;
      #endif
    }
    // Issue the command for both LEDs on the iRobot Create.
    byteTx(CmdLeds);
    byteTx($actorSymbol(LEDCommand));
    byteTx(0);
    byteTx(0);

    // Next handle the inputs for the command module LEDs.
    if($ref(commandModuleLED1)) {
      // True input received.
      // Note use of preprocessor for conditional code.
      #if ($val(toggle))
        // If the input differs from what it was
        // on the previous firing and is true, then
        // toggle the state of the LED.
        if ($actorSymbol(LED1FalseReceived)) {
          // First true since the last false. Toggle.
          PORTD ^= LED1;
        }
        // Indicate that a false has NOT been received.
        $actorSymbol(LED1FalseReceived) = 0;
      #else
        // toggle is false.
        // Just turn on the LED.
        // Note that "on" is low.
        PORTD &= ~LED1;
      #endif
    } else {
      // False input is received.
      // If toggle is false, then turn the LED off.
      // Note use of preprocessor for conditional code.
      #if ($val(toggle))
        // Indicate that a false has been received.
        $actorSymbol(LED1FalseReceived) = 1;
      #else
        // Just turn off the LED.
        // Note that "off" is high.
        PORTD |= LED1;
      #endif
    }
    // Finally, LED2.
    if($ref(commandModuleLED2)) {
      // True input received.
      // Note use of preprocessor for conditional code.
      #if $val(toggle)
        // If the input differs from what it was
        // on the previous firing and is true, then
        // toggle the state of the LED.
        if ($actorSymbol(LED2FalseReceived)) {
          // First true since the last false. Toggle.
          PORTD ^= LED2;
        }
        // Indicate that a false has NOT been received.
        $actorSymbol(LED2FalseReceived) = 0;
      #else
        // toggle is false.
        // Just turn on the LED.
        // Note that "on" is low.
        PORTD &= ~LED2;
      #endif
    } else {
      // False input is received.
      // If toggle is false, then turn the LED off.
      // Note use of preprocessor for conditional code.
      #if $val(toggle)
        // Indicate that a false has been received.
        $actorSymbol(LED2FalseReceived) = 1;
      #else
        // Just turn off the LED.
        // Note that "off" is high.
        PORTD |= LED2;
      #endif
      $ref(done) = 0x01;
    }
/**/

/***wrapupBlock***/
