/***preinitBlock***/
#ifndef SENSOR_ACTOR
#define SENSOR_ACTOR

#include "iRobotFunctions.c"
#include "oi.h"

//volatile int16_t distance = 0;
//volatile int16_t angle = 0;
//volatile uint8_t sensors[Sen0Size];
#endif
/**/

/***initBlock***/
  // Set the sensor data to be all zero.
  // This initializes the buffer that gets
  // filled by the interrupt service routine that
  // reads from the serial port.
  for(int i = 0; i < Sen6Size; i++) {
     sensors[i] = 0x0;
  }
/**/

/***fireBlock***/
if ($get(trigger)) {
  // Request Sensors Packet 2
  byteTx(CmdSensors);
  // Request packet 0, which has 26 bytes of information.
  byteTx(0);

  for(int i = 0; i < Sen0Size; i++) {
    sensors[i] = byteRx();
  }

    // Output sensor data.
  $put(bumpLeft, sensors[SenBumpDrop] & BumpLeft);
  $put(bumpRight, sensors[SenBumpDrop] & BumpRight);
  $put(wheelDropFront, sensors[SenBumpDrop] & WheelDropFront);
  $put(wheelDropLeft, sensors[SenBumpDrop] & WheelDropLeft);
  $put(wheelDropRight, sensors[SenBumpDrop] & WheelDropRight);
  $put(wall, sensors[SenWall]);
  $put(cliffLeft, sensors[SenCliffL]);
  $put(cliffFrontLeft, sensors[SenCliffFL]);
  $put(cliffFrontRight, sensors[SenCliffFR]);
  $put(cliffRight, sensors[SenCliffR]);
  $put(virtualWall, sensors[SenVWall]);
  $put(advanceButton, sensors[SenButton] & ButtonAdvance);
  $put(playButton, sensors[SenButton] & ButtonPlay);
  // Update accumulated distance and angle.
  distance += (int)((sensors[SenDist1] << 8) | sensors[SenDist0]);
  $put(distance, distance);
  angle += (int)((sensors[SenAng1] << 8) | sensors[SenAng0]);
  $put(angle, angle);

/* FIXME: Skipped the battery state
#define SenChargeState  16
#define SenVolt1        17
#define SenVolt0        18
#define SenCurr1        19
#define SenCurr0        20
#define SenTemp         21
#define SenCharge1      22
#define SenCharge0      23
#define SenCap1         24
#define SenCap0         25
*/

}

