/** Send a command to the iRobot Create to drive at
 *  the specified velocity, in mm per second, and at the
 *  specified angle, in mm.  The velocity can range from
 *  -500 to 500, and the radius from -2000 to 2000.

FIXME: Use portParameters.
FIXME: ExpertParameter STRAIGHT, etc.
FIXME: Details from the manual below:

The longer radii make Create drive straighter, while
the shorter radii make Create turn more. The radius is
measured from the center of the turning circle to the center
of Create. A Drive command with a positive velocity and a
positive radius makes Create drive forward while turning
toward the left. A negative radius makes Create turn toward
the right. Special cases for the radius make Create turn
in place or drive straight, as specified below. A negative
velocity makes Create drive backward.
NOTE: Internal and environmental restrictions may prevent
Create from accurately carrying out some drive commands.
For example, it may not be possible for Create to drive at
full speed in an arc with a large radius of curvature.
? Serial sequence: [137] [Velocity high byte] [Velocity low byte]
[Radius high byte] [Radius low byte]
? Available in modes: Safe or Full
? Changes mode to: No Change
? Drive data byte 1: Velocity (-500 ? 500 mm/s)
? Drive data byte 2: Radius (-2000 ? 2000 mm)
Special cases:
Straight = 32768 or 32767 = hex 8000 or 7FFF
Turn in place clockwise = hex FFFF
Turn in place counter-clockwise = hex 0001

 */

/***fileDependencies***/
oi.h
iRobotFunctions.c
/**/

/***preinitBlock***/
// Send Create drive commands in terms of velocity and radius
#ifndef _DRIVE_DRIVE
#define _DRIVE_DRIVE

#include "iRobotFunctions.c"
#include "oi.h"

// Global variables
volatile uint16_t timer_cnt = 0;
volatile uint8_t timer_on = 0;
volatile uint8_t sensors_flag = 0;
volatile uint8_t sensors_index = 0;
volatile uint8_t sensors_in[Sen6Size];
volatile uint8_t sensors[Sen6Size];
volatile int16_t distance = 0;
volatile int16_t angle = 0;

// Serial receive interrupt to store sensor values
SIGNAL(SIG_USART_RECV)
{
  uint8_t temp;

  temp = UDR0;

  if(sensors_flag)
  {
    sensors_in[sensors_index++] = temp;
    if(sensors_index >= Sen6Size)
      sensors_flag = 0;
  }
}


// Timer 1 interrupt to time delays in ms
SIGNAL(SIG_OUTPUT_COMPARE1A)
{
  if(timer_cnt)
    timer_cnt--;
  else
    timer_on = 0;
}

void drive(int16_t velocity, int16_t radius)
{
  byteTx(CmdDrive);
  byteTx((uint8_t)((velocity >> 8) & 0x00FF));
  byteTx((uint8_t)(velocity & 0x00FF));
  byteTx((uint8_t)((radius >> 8) & 0x00FF));
  byteTx((uint8_t)(radius & 0x00FF));
}

// Delay for the specified time in ms and update sensor values
void delayAndUpdateSensors(uint16_t time_ms)
{
  uint8_t temp;

  timer_on = 1;
  timer_cnt = time_ms;
  while(timer_on)
  {
    if(!sensors_flag)
    {
      for(temp = 0; temp < Sen6Size; temp++)
        sensors[temp] = sensors_in[temp];

      // Update running totals of distance and angle
      distance += (int)((sensors[SenDist1] << 8) | sensors[SenDist0]);
      angle += (int)((sensors[SenAng1] << 8) | sensors[SenAng0]);

      byteTx(CmdSensors);
      byteTx(6);
      sensors_index = 0;
      sensors_flag = 1;
    }
  }
}

#endif
/**/

/***initBlock***/
  // As a precaution, stop driving.
  // RadStraight = 32768, the code for no turns.
  drive(0, RadStraight);
/**/

/***fireBlock***/
if($get(input)==1){

 drive($get(velocity), $get(radius));
    while(-(angle) <= $get(RotateAngle)) {
    drive($get(velocity), $get(radius));
	  // Wait 20 ms
  	delay10ms(2);
  // Request Sensors Packet 2
  byteTx(CmdSensors);
  // Request packet 0, which has 26 bytes of information.
  byteTx(0);

  for(int i = 0; i < Sen0Size; i++) {
    sensors[i] = byteRx();
  }
  // Update accumulated distance and angle.
  angle += (int)((sensors[SenAng1] << 8) | sensors[SenAng0]);
   }



/*
  drive($get(velocity), $get(radius));
  while(-(angle) <= $get(RotateAngle)) {
    // wait a little more than one robot tick for sensors to update
    delayAndUpdateSensors(20);
  }
*/  

  drive(0, RadStraight);
  delay10ms(30);
  //delayAndUpdateSensors(20);
  angle=0;

  $put(done, true);
} else  {
  drive(0, RadStraight);
  //delayAndUpdateSensors(20);
  delay10ms(30);
  angle=0;
  $put(done, true);
}


